list-view-config.query-select:

PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX afn:  <http://jena.hpl.hp.com/ARQ/function#>
PREFIX bibo: <http://purl.org/ontology/bibo/> 
PREFIX duke: <http://vivo.duke.edu/vivo/ontology/duke-extension#>
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>

SELECT DISTINCT ?subclass 
                ?authorship
                ?infoResource
                ?dateTime
     (IF(bound(?pCitation), ?pCitation, ?chicagoCitation) AS ?preferredCitation)
                ?doi
                ?onlineContent
                ?linkToItem
WHERE {
    ?subject ?property ?authorship .
    ?authorship core:relates ?infoResource .
    ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030> .                      
    OPTIONAL {
      ?subject duke:preferredCitationFormat ?preferredFormat.
      ?infoResource ?preferredFormat ?pCitation.
    }
    OPTIONAL {
      ?infoResource duke:chicagoCitation  ?chicagoCitation.
    }
    OPTIONAL { ?infoResource duke:onlineContent  ?onlineContent }
    OPTIONAL { ?infoResource duke:linkToItem     ?linkToItem }
    OPTIONAL { ?infoResource bibo:doi ?doi  }
    OPTIONAL {  ?infoResource vitro:mostSpecificType ?subclass .  }
    OPTIONAL { ?infoResource core:dateTimeValue ?dateTimeValue .
               ?dateTimeValue core:dateTime ?dateTime
    }

    FILTER ( bound(?infoResource) )

} ORDER BY ?subclass  DESC(?dateTime) ?preferredCitation

list-view-config.query-construct:
PREFIX duke: <http://vivo.duke.edu/vivo/ontology/duke-extension#>
CONSTRUCT {
    ?subject duke:preferredCitationFormat ?preferredFormat
} WHERE {
    ?subject duke:preferredCitationFormat ?preferredFormat
}

PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX bibo: <http://purl.org/ontology/bibo/> 
PREFIX duke: <http://vivo.duke.edu/vivo/ontology/duke-extension#>
CONSTRUCT { 
    ?subject ?property ?authorship .  
    ?authorship a core:Authorship . 
    ?authorship ?authorshipProperty ?authorshipValue .
    ?authorship core:relates ?infoResource .
    ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030> .
    ?infoResource ?infoResourceProperty ?infoResourceValue .
    ?infoResource duke:linkToItem    ?linkToItem .
} WHERE {
    {
       ?subject ?property ?authorship .
       ?authorship a core:Authorship 
    }
    UNION {
       ?subject ?property ?authorship .
       ?authorship a core:Authorship .
       ?authorship ?authorshipProperty ?authorshipValue 
    } UNION {
       ?subject ?property ?authorship .
       ?authorship a core:Authorship .
       ?authorship core:relates ?infoResource .
       ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030>  
    } UNION {
       ?subject ?property ?authorship .
       ?authorship a core:Authorship .
       ?authorship core:relates ?infoResource .
       ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030> .
       ?infoResource ?infoResourceProperty ?infoResourceValue
    } UNION {
       ?subject ?property ?authorship .
       ?authorship a core:Authorship .
       ?authorship core:relates ?infoResource .
       ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030> .
       ?infoResource duke:linkToItem ?linkToItem
    }
}
