list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX afn: <http://jena.hpl.hp.com/ARQ/function#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX obo: <http://purl.obolibrary.org/obo/>
SELECT DISTINCT ?roleTypeLabel # send the property to the template, since this view supports multiple role properties ?property ?role ?roleLabel ?indivInRole (afn:localname(?indivInRole) AS ?indivName) ?indivLabel WHERE {
?subject ?property ?role. ?role a core:InvestigatorRole.
OPTIONAL { ?role rdfs:label ?roleLabel }
# We need ?subclass in the uncollated query to get the roleTypeLabel # for roles that have no label (e.g., InvestigatorRole and its subclasses). OPTIONAL { ?role vitro:mostSpecificType ?subclass . OPTIONAL { ?subclass rdfs:label ?roleTypeLabel } } OPTIONAL { ?role obo:RO_0000052 ?indivInRole. OPTIONAL { ?indivInRole rdfs:label ?indivLabel } }
} ORDER BY ?roleLabel ?indivLabel ?roleTypeLabel ?indivName

list-view-config.query-construct:
PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
PREFIX obo:   <http://purl.obolibrary.org/obo/>
CONSTRUCT {
    ?subject ?property ?role .
    ?role ?roleProperty ?roleValue .
    ?role vitro:mostSpecificType ?subclass .
    ?subclass rdfs:label ?roleTypeLabel .
    ?indivInRole rdfs:label ?indivLabel
} WHERE {
    {
       ?subject ?property ?role
    } UNION {
       ?subject ?property ?role .
       ?role ?roleProperty ?roleValue
    } UNION {
       ?subject ?property ?role .
       ?role vitro:mostSpecificType ?subclass
    } UNION {
       ?subject ?property ?role .
       ?role vitro:mostSpecificType ?subclass .
       ?subclass rdfs:label ?roleTypeLabel
    } UNION {
       ?subject ?property ?role .
       ?role obo:RO_0000052 ?indivInRole .
       ?indivInRole rdfs:label ?indivLabel
    }
}
