list-view-config.query-select:
PREFIX afn: <http://jena.hpl.hp.com/ARQ/function#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX foaf: <http://xmlns.com/foaf/0.1/> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX duke: <http://vivo.duke.edu/vivo/ontology/duke-extension#> PREFIX dukeart: <http://vivo.duke.edu/vivo/ontology/duke-art-extension#>
SELECT DISTINCT ?subclass ?artisticRelationship ?artCreator ?artCreatorName ?roles ?isFeaturedRole WHERE { ?subject ?property ?artisticRelationship . OPTIONAL { ?artisticRelationship core:relates ?artCreator . ?artCreator a foaf:Person . ?artCreator rdfs:label ?artCreatorName . FILTER(?artCreator != ?subject) } OPTIONAL { ?artisticRelationship dukeart:roles ?roles } PTIONAL { ?artisticRelationship dukeart:isFeaturedRole ?isFeaturedRole }O
} ORDER BY ?subclass ?artCreatorName

list-view-config.query-construct:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>    
PREFIX dukeart: <http://vivo.duke.edu/vivo/ontology/duke-art-extension#>
CONSTRUCT {
    ?subclass rdfs:subClassOf dukeart:ArtisticWork 
} WHERE {
    ?subclass rdfs:subClassOf dukeart:ArtisticWork 
}

PREFIX dukeart: <http://vivo.duke.edu/vivo/ontology/duke-art-extension#>
CONSTRUCT {
    ?subject ?property ?artisticRelationship .
    ?artisticRelationship ?relationshipProperty ?RelationshipValue .
} WHERE {
    {
        ?subject ?property ?artisticRelationship .
        ?artisticRelationship a dukeart:ArtisticRelationship.
    } UNION {
        ?subject ?property ?artisticRelationship .
        ?artisticRelationship a dukeart:ArtisticRelationship.
        ?artisticRelationship ?relationshipProperty ?RelationshipValue
    }
}

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX dukeart: <http://vivo.duke.edu/vivo/ontology/duke-art-extension#>
PREFIX foaf: <http://xmlns.com/foaf/0.1/>
PREFIX core: <http://vivoweb.org/ontology/core#>
CONSTRUCT {
    ?subject ?property ?artisticRelationship .
    ?artisticRelationship core:relates ?artCreator .
    ?artCreator a ?artCreatorType .
    ?artCreator rdfs:label ?artCreatorName .
} WHERE {
    {
        ?subject ?property ?artisticRelationship .
        ?artisticRelationship a dukeart:ArtisticRelationship.
    } UNION {
        ?subject ?property ?artisticRelationship .
        ?artisticRelationship a dukeart:ArtisticRelationship.
        ?artisticRelationship core:relates ?artCreator .
        ?artCreator a foaf:Person .
    } UNION {
        ?subject ?property ?artisticRelationship .
        ?artisticRelationship a dukeart:ArtisticRelationship.
        ?artisticRelationship core:relates ?artCreator .
        ?artCreator a foaf:Person .
        ?artCreator rdfs:label ?artCreatorName .
    } UNION {
        ?subject ?property ?artisticRelationship .
        ?artisticRelationship a dukeart:ArtisticRelationship.
        ?artisticRelationship core:relates ?artCreator .
        ?artCreator a foaf:Person .
        ?artCreator a ?artCreatorType .
    }
}
